mapper.select:
select
  id, name, position_id, phone_number, id_card_number, wage, entry_date, separation_date, is_separation
from aquacltur.staff


        and id = #{id}


        and name = #{name}


        and position_id = #{positionId}


        and phone_number = #{phoneNumber}


        and id_card_number = #{idCardNumber}


        and wage = #{wage}


        and entry_date = #{entryDate}


        and separation_date = #{separationDate}


        and is_separation = #{isSeparation}
